const WORK_TIME_MINUTES = 1;  // Change this to 25 for production
const BREAK_TIME_MINUTES = 1;  // Change this to 5 for production

let timeLeft;
let timerId = null;
let isWorkTime = true;

const minutesDisplay = document.getElementById('minutes');
const secondsDisplay = document.getElementById('seconds');
const startButton = document.getElementById('start');
const pauseButton = document.getElementById('pause');
const resetButton = document.getElementById('reset');
const statusText = document.getElementById('status-text');
const toggleButton = document.getElementById('toggle-timer');

function fireConfetti() {
    // Fire multiple confetti bursts
    const duration = 3000;
    const end = Date.now() + duration;

    // Fire from multiple angles
    const angles = [60, 120, 180];
    angles.forEach(angle => {
        confetti({
            particleCount: 100,
            angle: angle,
            spread: 60,
            origin: { x: 0.5, y: 0.8 },
            colors: ['#ff0000', '#00ff00', '#0000ff', '#ffff00', '#ff00ff']
        });
    });

    // Create continuous firework effect
    (function frame() {
        confetti({
            particleCount: 20,
            angle: 60,
            spread: 55,
            origin: { x: 0 },
            colors: ['#ff0000', '#00ff00', '#0000ff']
        });
        confetti({
            particleCount: 20,
            angle: 120,
            spread: 55,
            origin: { x: 1 },
            colors: ['#ff0000', '#00ff00', '#0000ff']
        });

        if (Date.now() < end) {
            requestAnimationFrame(frame);
        }
    }());
}

function playCelebrationSound() {
    const audio = new Audio('data:audio/wav;base64,');
    audio.play();
}

function updateDisplay() {
    const minutes = Math.floor(timeLeft / 60);
    const seconds = timeLeft % 60;
    minutesDisplay.textContent = minutes.toString().padStart(2, '0');
    secondsDisplay.textContent = seconds.toString().padStart(2, '0');

    // Calculate and update the progress
    const totalTime = isWorkTime ? WORK_TIME_MINUTES * 60 : BREAK_TIME_MINUTES * 60;
    const progress = (timeLeft / totalTime) * 100;
    document.querySelector('.bomb-body').style.setProperty('--progress', `${progress}%`);
}

function switchMode() {
    isWorkTime = !isWorkTime;
    timeLeft = isWorkTime ? WORK_TIME_MINUTES * 60 : BREAK_TIME_MINUTES * 60;
    statusText.textContent = isWorkTime ? 'Work Time' : 'Break Time';

    // If we're switching to break time (meaning work timer just ended)
    if (!isWorkTime) {
        // MASSIVE FUCKING CELEBRATION
        fireConfetti();
        playCelebrationSound();

        // Create some wild background flashing
        let flashCount = 0;
        const maxFlashes = 10;
        const flash = setInterval(() => {
            document.body.style.backgroundColor = flashCount % 2 === 0 ? '#ff0000' : '#ffff00';
            flashCount++;
            if (flashCount >= maxFlashes) {
                clearInterval(flash);
                document.body.style.backgroundColor = '#f0f0f0';  // Reset to original color
            }
        }, 200);
    }

    updateDisplay();
}

function toggleTimer() {
    if (timerId === null) {
        // Start timer
        if (!timeLeft) {
            timeLeft = WORK_TIME_MINUTES * 60;
        }
        timerId = setInterval(() => {
            timeLeft--;
            updateDisplay();
            if (timeLeft === 0) {
                clearInterval(timerId);
                timerId = null;
                toggleButton.innerHTML = '<i class="fas fa-play"></i>';
                switchMode();
            }
        }, 1000);
        toggleButton.innerHTML = '<i class="fas fa-pause"></i>';
    } else {
        // Pause timer
        clearInterval(timerId);
        timerId = null;
        toggleButton.innerHTML = '<i class="fas fa-play"></i>';
    }
}

function resetTimer() {
    clearInterval(timerId);
    timerId = null;
    isWorkTime = true;
    timeLeft = WORK_TIME_MINUTES * 60;
    statusText.textContent = 'Work Time';
    toggleButton.innerHTML = '<i class="fas fa-play"></i>';
    updateDisplay();
}

toggleButton.addEventListener('click', toggleTimer);
resetButton.addEventListener('click', resetTimer);

// Initialize the timer
resetTimer();
